numero = (5, 8, 12, 8, 7, 8, 3)
#para ser uma tupla precisa estar entre parenteses
print("tupla original:", numero)
#imprimindo para o usúario a tupla original, sem manipulaçãoes
print("Elemento da tupla", len(numero))
print("acessando pelo indicie", numero[2])
#escolhendo qual item será mostrado através do indicie, lembrando que começa do zero
print("fazendo um slice do 2 ao 5", numero[2:5])
#o slicing ele não pega o ultimo item defenido no recorte
print("Quantas vezes o numero 8 repete", numero.count(8))
print("posição da primeira ocorrencia do  numero 7", numero.index(7))  
minimo_tupla = min(numero)#menor numero da tupla
print("minimo_tupla")
maximo = max(numero)#maior numero da tupla
print("maximo_tupla")
sum_tupla = sum(numero)#soma da tupla
print(sum_tupla)
sorted_tupla = sorted(numero)#ordena a tupla
print(sorted_tupla)
print("o numero 4 esta na tupla?", 4 in numero)
numero2 = (15, 20)
tupla_concatenada = numero + numero2
print("a concatenação das tuplas 1 e 2 resulta na nova tupla", tupla_concatenada)                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                         
tupla_duplicada = numero * 2
print(tupla_duplicada)